mapper.select:
SELECT *
FROM USERS
WHERE NAME = #{name} AND PHONE = #{phone}

SELECT *
FROM USERS
WHERE NAME = #{name} AND PHONE = #{phone} AND USER_ID = #{userId}

mapper.update:
UPDATE USERS
SET 
	PASSWORD = #{hashPwd}
WHERE USER_ID = #{userId}

UPDATE MEDIA
SET
	TITLE = #{fileName},
	STOR_URL = #{path},
	USER_ID = #{userId}
WHERE MEDIA_ID = #{mediaId} AND USER_ID = #{userId}

UPDATE USERS 
SET GEN_CNT = NVL(GEN_CNT, 0) + 1 
WHERE USER_ID = #{userId}
