svg.script:
(function () {
  function fitViewBox() {
    try {
      const svg = document.getElementById('korea-map');
      const root = document.getElementById('map-root');
      if (!svg || !root) return;

      const bb = root.getBBox();
      const pad = 6; // 여백 조금만

      svg.setAttribute(
        'viewBox',
        (bb.x - pad) + ' ' + (bb.y - pad) + ' ' + (bb.width + pad * 2) + ' ' + (bb.height + pad * 2)
      );
      svg.setAttribute('preserveAspectRatio', 'xMidYMid meet');
    } catch (e) {}
  }

  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', fitViewBox);
  } else {
    fitViewBox();
  }
  window.addEventListener('load', fitViewBox);
})();
